import React, { useState } from "react";
import Button from "../button/Button";
import Logo from "../logo/Logo";
import NavLinks from "../navigation/NavLinks";

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const MenuItems = [
    { name: 'Home', href: '#' },
    { name: 'Properties', href: '#' },
    { name: 'Agents', href: '#' },
    { name: 'Blog', href: '#' },
  ];

  return (
    <header className="relative flex-wrap items-center content-center justify-between w-full p-10 animate-fade-down animate-delay-300 animate-once md:flex">
      {/* Logo */}
      <Logo className="w-16 h-16" />

      {/* Mobile Menu Button */}
      <div className="sm:ml-auto block md:hidden text-end mt-[-40px]">
        <button onClick={toggleMenu} className="focus:outline-none">
          <svg
            className="w-8 h-8 text-cyan-600"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4 6h16M4 12h16m-7 6h7"
            />
          </svg>
        </button>
      </div>

      {/* Navigation Links */}
      <NavLinks navItems={MenuItems} className="mb-2 md:mx-10 md:mb-0" />

      {/* Login Button (hidden on small screens) */}
      <Button onClick={() => console.log('Button clicked')} className="hidden md:block">
        Login
      </Button>
    </header>
  );
};

export default Header;
