import React, { useState } from "react";
import Button from "../button/Button";
import Logo from "../logo/Logo";
import NavLinks from "../navigation/NavLinks";

const Header = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const MenuItems = [
        { name: 'Home', href: '#' },
        { name: 'Properties', href: '#' },
        { name: 'Agents', href: '#' },
        { name: 'Blog', href: '#' },
    ];

    return (
        <header className="fixed top-0 z-50 w-full px-4 py-4 bg-gray-100 shadow-lg md:px-10 rounded-b-3xl">
            {/* Logo */}
            <div className="flex items-center justify-between max-w-6xl mx-auto">
                <Logo className="flex items-center w-16 h-16" />

                {/* Mobile Menu Button */}
                <div className="block md:hidden">
                    <button onClick={toggleMenu} className="focus:outline-none">
                        <svg
                            className="w-8 h-8 text-cyan-600"
                            fill="none"
                            stroke="currentColor"
                            viewBox="0 0 24 24"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M4 6h16M4 12h16m-7 6h7"
                            />
                        </svg>
                    </button>
                </div>

                {/* Menu */}
                <nav
                    className={`${
                        isMenuOpen ? "block" : "hidden"
                    } md:flex items-center justify-between w-full md:w-auto`}
                >
                    <ul className="flex flex-col space-y-4 font-semibold uppercase md:flex-row md:space-y-0 md:space-x-8 text-cyan-600">
                        <NavLinks navItems={MenuItems} />
                    </ul>
                </nav>

                {/* Login Button */}
                <div className="hidden md:block">
                    <Button onClick={() => console.log("Login button clicked")}>
                        Login
                    </Button>
                </div>
            </div>

            {/* Mobile Login Button */}
            {isMenuOpen && (
                <div className="mt-4 md:hidden">
                    <Button onClick={() => console.log("Login button clicked")}>
                        Login
                    </Button>
                </div>
            )}
        </header>
    );
};

export default Header;
