import React from 'react'

const ImgItems= [
  { name: 'Hotel', url: 'banner1.avif' },
  { name: 'Home', url: 'banner2.avif' },
  { name: 'Villa', url: 'banner3.avif' },
  { name: 'Drink & Food', url: 'banner4.avif' }, 
];
const Banner = () => {
  return (
    <div className="flex h-[500px] overflow-hidden">
      {ImgItems.map()}
      <div className="relative flex-1 mr-[-20%] hover:flex-[1.25] transition-[flex] duration-150 ease-linear">
        <a href="#link">
          <div className="absolute bottom-4 left-4 text-white z-10 text-[clamp(12px,5vw,32px)]">Hotel</div>
          <img className="object-cover w-full h-full transition duration-200 grayscale hover:grayscale-0" src="banner1.avif" alt=""/>
        </a>
      </div>
    </div>

  )
}

export default Banner
