import React, { useState, useEffect, useRef } from "react";
import Button from "../button/Button";
import Logo from "../logo/Logo";
import NavLinks from "../navigation/NavLinks";
import * as THREE from "three";

const Header = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const mountRef = useRef(null);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    useEffect(() => {
        // Setup Three.js scene, camera, renderer
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf5f5f5);

        const camera = new THREE.PerspectiveCamera(
            75,
            window.innerWidth / window.innerHeight,
            0.1,
            1000
        );
        camera.position.z = 5;

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        <mountRef className="current"></mountRef>.appendChild(renderer.domElement);

        // Geometry (Rounded Rectangle as Header)
        const geometry = new THREE.BoxGeometry(4, 1, 0.2);
        const material = new THREE.MeshStandardMaterial({ color: 0xffffff });
        const headerMesh = new THREE.Mesh(geometry, material);
        scene.add(headerMesh);

        // Lighting
        const light = new THREE.PointLight(0xffffff, 1, 100);
        light.position.set(5, 5, 5);
        scene.add(light);

        const ambientLight = new THREE.AmbientLight(0x404040, 0.5);
        scene.add(ambientLight);

        const animate = function () {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        };

        animate();

        return () => {
            mountRef.current.removeChild(renderer.domElement);
        };
    }, []);

    const MenuItems = [
        { name: 'Home', href: '#' },
        { name: 'Properties', href: '#' },
        { name: 'Agents', href: '#' },
        { name: 'Blog', href: '#' },
    ];

    return (
        <header className="relative w-full h-screen">
            {/* Three.js background */}
            <div className="absolute top-0 left-0 w-full h-full" ref={mountRef}></div>
            
            {/* Content Overlay */}
            <div className="relative z-10 flex items-center justify-between px-10 py-5 bg-transparent">
                {/* Logo */}
                <Logo className="flex items-center justify-center w-16 h-16 my-auto"></Logo>

                {/* Menu */}
                <div className="block md:hidden text-end">
                    <button onClick={toggleMenu} className="focus:outline-none">
                        <svg
                            className="w-8 h-8 text-cyan-600"
                            fill="none"
                            stroke="currentColor"
                            viewBox="0 0 24 24"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M4 6h16M4 12h16m-7 6h7"
                            />
                        </svg>
                    </button>
                </div>

                <div className="hidden md:flex w-[800px] bg-white shadow-lg rounded-b-full items-center justify-around h-16">
                    <NavLinks navItems={MenuItems} className="font-semibold uppercase text-cyan-600" />
                </div>

                {/* Login Button */}
                <Button className="hidden md:block" onClick={() => console.log('Button clicked') }>Login</Button>
            </div>
        </header>
    );
};

export default Header;
